const PuzzlePage = ({ puzzle, onPuzzleSolved, deductXp }) => {
    const [timeRemaining, setTimeRemaining] = useState(puzzle.timeLimit * 60);
    const [revealedHints, setRevealedHints] = useState([]);
    const [userAnswer, setUserAnswer] = useState('');
    const [submissionStatus, setSubmissionStatus] = useState('idle'); // 'idle', 'correct', 'incorrect'
    const [isSolutionRevealed, setIsSolutionRevealed] = useState(false);

    useEffect(() => {
        setTimeRemaining(puzzle.timeLimit * 60);
        setRevealedHints([]);
        setUserAnswer('');
        setSubmissionStatus('idle');
        setIsSolutionRevealed(false);

        const timer = setInterval(() => {
            setTimeRemaining(prev => (prev > 0 ? prev - 1 : 0));
        }, 1000);
        return () => clearInterval(timer);
    }, [puzzle]);

    const formatTime = (seconds) => {
        const minutes = Math.floor(seconds / 60);
        const remainingSeconds = seconds % 60;
        return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
    };

    const revealHint = () => {
        if (puzzle.hints && revealedHints.length < puzzle.hints.length) {
            setRevealedHints([...revealedHints, puzzle.hints[revealedHints.length]]);
            deductXp(10);
        }
    };

    const revealSolution = () => {
        if (!isSolutionRevealed) {
            setIsSolutionRevealed(true);
            deductXp(50);
        }
    };

    const handleAnswerSubmit = async (e) => {
        e.preventDefault();
        if(!userAnswer) return;
        try {
            await api.post(`/puzzles/${puzzle.id}/solve`, { userAnswer });
            setSubmissionStatus('correct');
            onPuzzleSolved(puzzle);
        } catch (err) {
            setSubmissionStatus('incorrect');
            setUserAnswer('');
        }
    };

    const handleTryAgain = () => setSubmissionStatus('idle');
    const handleNextPuzzle = () => onPuzzleSolved(puzzle, true);

    const difficultyStyles = {
        Easy: 'bg-green-100 text-green-800',
        Medium: 'bg-yellow-100 text-yellow-800',
        Hard: 'bg-red-100 text-red-800',
    };

    return (
        <div className="bg-gray-50 dark:bg-black min-h-screen py-12">
            <div className="container mx-auto px-4 sm:px-6 lg:px-8">
                <button onClick={() => onPuzzleSolved(null, true)} className="flex items-center gap-2 text-sm font-medium text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white mb-8">
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><line x1="19" y1="12" x2="5" y2="12"></line><polyline points="12 19 5 12 12 5"></polyline></svg>
                    Back to Puzzles
                </button>

                <div className="grid grid-cols-1 lg:grid-cols-5 gap-8">
                    <div className="lg:col-span-3 space-y-8">
                         <div className="bg-white dark:bg-gray-900 border border-gray-200 dark:border-gray-800 rounded-xl p-8">
                            <h1 className="text-3xl font-bold text-gray-900 dark:text-white">{puzzle.title}</h1>
                            <p className="mt-4 text-lg text-gray-600 dark:text-gray-300">{puzzle.question}</p>
                        </div>

                        <div className="bg-white dark:bg-gray-900 border border-gray-200 dark:border-gray-800 rounded-xl p-8">
                            <h2 className="text-xl font-semibold text-gray-800 dark:text-gray-200 mb-4">Your Answer</h2>
                            <form onSubmit={handleAnswerSubmit}>
                                <div className="flex flex-col sm:flex-row gap-4">
                                    <input 
                                        type="text" 
                                        placeholder="Enter your answer..." 
                                        value={userAnswer}
                                        onChange={(e) => setUserAnswer(e.target.value)}
                                        disabled={submissionStatus === 'correct'}
                                        className="flex-grow px-4 py-3 bg-gray-50 dark:bg-gray-800 border border-gray-300 dark:border-gray-700 rounded-lg focus:ring-2 focus:ring-black dark:focus:ring-white focus:border-black dark:focus:border-white text-gray-800 dark:text-gray-200 disabled:opacity-50" 
                                    />
                                    <button 
                                        type="submit"
                                        disabled={submissionStatus === 'correct'}
                                        className="px-6 py-3 text-base font-semibold text-white bg-black rounded-lg hover:bg-gray-800 dark:bg-white dark:text-black dark:hover:bg-gray-200 disabled:opacity-50 disabled:cursor-not-allowed"
                                    >
                                        Submit Answer
                                    </button>
                                </div>
                            </form>
                            {submissionStatus === 'correct' && (
                                <div className="mt-4 p-4 rounded-lg bg-green-50 dark:bg-green-900/20 text-green-700 dark:text-green-400 text-center">
                                    <p className="font-semibold">Correct!</p>
                                    <button onClick={handleNextPuzzle} className="mt-2 px-4 py-2 text-sm font-medium text-white bg-green-600 rounded-lg hover:bg-green-700">Next Puzzle</button>
                                </div>
                            )}
                             {submissionStatus === 'incorrect' && (
                                <div className="mt-4 p-4 rounded-lg bg-red-50 dark:bg-red-900/20 text-red-700 dark:text-red-400 text-center">
                                    <p className="font-semibold">Incorrect, please try again.</p>
                                    <button onClick={handleTryAgain} className="mt-2 px-4 py-2 text-sm font-medium text-red-800 bg-red-200 rounded-lg hover:bg-red-300">Try Again</button>
                                </div>
                            )}
                        </div>
                    </div>

                    <div className="lg:col-span-2 space-y-8">
                        <div className="bg-white dark:bg-gray-900 border border-gray-200 dark:border-gray-800 rounded-xl p-6">
                            <h3 className="font-semibold text-gray-800 dark:text-gray-200 mb-4">Details</h3>
                            <div className="space-y-3 text-sm">
                                <div className="flex justify-between items-center">
                                    <span className="text-gray-500 dark:text-gray-400">Difficulty</span>
                                    <span className={`font-semibold px-2 py-0.5 rounded-full ${difficultyStyles[puzzle.difficulty]}`}>{puzzle.difficulty}</span>
                                </div>
                                <div className="flex justify-between items-center">
                                    <span className="text-gray-500 dark:text-gray-400">Category</span>
                                    <span className="font-semibold text-gray-800 dark:text-gray-200">{puzzle.category}</span>
                                </div>
                                <div className="flex justify-between items-center">
                                    <span className="text-gray-500 dark:text-gray-400">Success Rate</span>
                                    <span className="font-semibold text-gray-800 dark:text-gray-200">{puzzle.successRate}%</span>
                                </div>
                            </div>
                        </div>

                        <div className="bg-white dark:bg-gray-900 border border-gray-200 dark:border-gray-800 rounded-xl p-6 text-center">
                            <div className="flex items-center justify-center gap-2 text-gray-500 dark:text-gray-400">
                                <ClockIcon />
                                <h3 className="text-lg font-medium">Time Remaining</h3>
                            </div>
                            <p className="text-5xl font-bold text-gray-800 dark:text-gray-200 my-3">{formatTime(timeRemaining)}</p>
                            <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
                                <div className="bg-black dark:bg-white h-2 rounded-full" style={{ width: `${(timeRemaining / (puzzle.timeLimit * 60)) * 100}%` }}></div>
                            </div>
                        </div>

                        <div className="bg-white dark:bg-gray-900 border border-gray-200 dark:border-gray-800 rounded-xl p-6">
                            <h3 className="font-semibold text-gray-800 dark:text-gray-200 mb-4">Hints ({revealedHints.length}/{puzzle.hints?.length || 0})</h3>
                            <div className="space-y-3">
                                {revealedHints.map((hint, index) => (
                                    <p key={index} className="text-sm text-gray-600 dark:text-gray-300 p-3 bg-gray-50 dark:bg-gray-800/50 rounded-lg">
                                        {hint}
                                    </p>
                                ))}
                                {puzzle.hints && revealedHints.length < puzzle.hints.length && (
                                    <button onClick={revealHint} className="w-full mt-2 py-2 text-sm font-semibold text-black dark:text-white border border-black dark:border-white rounded-lg hover:bg-gray-100 dark:hover:bg-gray-800">
                                        Reveal Hint (-10 XP)
                                    </button>
                                )}
                            </div>
                        </div>

                        <div className="bg-white dark:bg-gray-900 border border-gray-200 dark:border-gray-800 rounded-xl p-6">
                            <h3 className="font-semibold text-gray-800 dark:text-gray-200 mb-4">Solution</h3>
                            {isSolutionRevealed ? (
                                <p className="text-sm text-gray-600 dark:text-gray-300 p-3 bg-gray-50 dark:bg-gray-800/50 rounded-lg font-semibold">{puzzle.answer}</p>
                            ) : (
                                <button onClick={revealSolution} className="w-full py-2 text-sm font-semibold text-red-600 border border-red-600 rounded-lg hover:bg-red-50 dark:hover:bg-red-900/20">
                                    Reveal Solution (-50 XP)
                                </button>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

