        title: '',
        question: '',
        answer: '',
        difficulty: 'Easy',
        category: 'Logic',
        hints: '',
        timeLimit: 5,
        featured: false,
    });

    const handleInputChange = (e) => {
        const { name, value, type, checked } = e.target;
        setNewPuzzle(prev => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const puzzleToAdd = {
            ...newPuzzle,
            hints: newPuzzle.hints.split(',').map(h => h.trim()).filter(h => h),
