                    </div>
                    <div className="bg-white dark:bg-gray-900 border border-gray-200 dark:border-gray-800 rounded-xl p-8 shadow-sm">
                        <h2 className="text-xl font-semibold text-gray-800 dark:text-gray-200 mb-6">Add New Puzzle</h2>
                        <form onSubmit={handleSubmit} className="space-y-6">
                             <div>
                                <label htmlFor="title" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Title</label>
                                <input type="text" name="title" id="title" value={newPuzzle.title} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 dark:border-gray-700 rounded-md shadow-sm bg-white dark:bg-gray-800" required />
                            </div>
                            <div>
                                <label htmlFor="question" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Question</label>
                                <textarea name="question" id="question" rows="4" value={newPuzzle.question} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 dark:border-gray-700 rounded-md shadow-sm bg-white dark:bg-gray-800" required></textarea>
                            </div>
                            <div>
                                <label htmlFor="answer" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Answer</label>
                                <input type="text" name="answer" id="answer" value={newPuzzle.answer} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 dark:border-gray-700 rounded-md shadow-sm bg-white dark:bg-gray-800" required />
                            </div>
                            <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
                                <div>
                                    <label htmlFor="difficulty" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Difficulty</label>
                                    <select id="difficulty" name="difficulty" value={newPuzzle.difficulty} onChange={handleInputChange} className="mt-1 block w-full pl-3 pr-10 py-2 border-gray-300 dark:border-gray-700 rounded-md bg-white dark:bg-gray-800">
                                        <option>Easy</option>
                                        <option>Medium</option>
                                        <option>Hard</option>
                                    </select>
                                </div>
                                <div>
                                    <label htmlFor="category" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Category</label>
                                    <select id="category" name="category" value={newPuzzle.category} onChange={handleInputChange} className="mt-1 block w-full pl-3 pr-10 py-2 border-gray-300 dark:border-gray-700 rounded-md bg-white dark:bg-gray-800">
                                        <option>Math</option>
                                        <option>Logic</option>
                                        <option>Visual</option>
                                    </select>
                                </div>
                            </div>
                             <div>
                                <label htmlFor="hints" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Hints (comma-separated)</label>
                                <input type="text" name="hints" id="hints" value={newPuzzle.hints} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 dark:border-gray-700 rounded-md shadow-sm bg-white dark:bg-gray-800" />
                            </div>
                             <div>
                                <label htmlFor="timeLimit" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Time Limit (minutes)</label>
                                <input type="number" name="timeLimit" id="timeLimit" value={newPuzzle.timeLimit} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 dark:border-gray-700 rounded-md shadow-sm bg-white dark:bg-gray-800" />
                            </div>
                            <div className="flex items-start">
                                <div className="flex items-center h-5">
                                    <input id="featured" name="featured" type="checkbox" checked={newPuzzle.featured} onChange={handleInputChange} className="h-4 w-4 text-black border-gray-300 rounded bg-gray-50 dark:bg-gray-700" />
                                </div>
                                <div className="ml-3 text-sm">
                                    <label htmlFor="featured" className="font-medium text-gray-700 dark:text-gray-300">Mark as Featured</label>
                                </div>
                            </div>
                            <div>
                                <button type="submit" className="w-full flex justify-center py-3 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-black hover:bg-gray-800 dark:bg-white dark:text-black dark:hover:bg-gray-200">
                                    Add Puzzle
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

const RestrictedPage = () => (
    <div className="bg-gray-50 dark:bg-black min-h-screen flex items-center justify-center">
        <div className="text-center">
            <h1 className="text-4xl font-bold text-gray-900 dark:text-white">Access Denied</h1>
            <p className="mt-4 text-gray-600 dark:text-gray-300">You do not have permission to view this page. Please log in as an admin.</p>
        </div>
    </div>
);

const Footer = () => (
    <footer className="bg-gray-100 dark:bg-gray-950/50 border-t border-gray-200 dark:border-gray-800">
        <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-4 text-center text-sm text-gray-500 dark:text-gray-400">
            <p>&copy; 2025 Wits. All rights reserved.</p>
        </div>
    </footer>
);

// --- MAIN APP COMPONENT ---
export default function App() {
    const [currentPage, setCurrentPage] = useState('home');
    const [selectedPuzzle, setSelectedPuzzle] = useState(null);
    const [isDarkMode, setIsDarkMode] = useState(false);

    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [isAdmin, setIsAdmin] = useState(false);
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    const [puzzles, setPuzzles] = useState([]);

    const handleLogout = useCallback(() => {
        setAuthToken(null);
        setUser(null);
        setIsAuthenticated(false);
        setIsAdmin(false);
        setCurrentPage('home');
    }, []);

    const loadUser = useCallback(async () => {
        try {
            const res = await api.get('/auth');
            setUser(res.data);
            setIsAuthenticated(true);
            setIsAdmin(res.data.isAdmin);
        } catch (err) {
            handleLogout();
        } finally {
            setLoading(false);
        }
    }, [handleLogout]);

    const fetchPuzzles = useCallback(async () => {
        try {
            const res = await api.get('/puzzles');
            const puzzlesWithStatus = res.data.map(p => ({...p, id: p._id, status: 'not_started' }));
            setPuzzles(puzzlesWithStatus);
        } catch (err) {
            console.error("Error fetching puzzles", err);
        }
    }, []);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            setAuthToken(token);
            loadUser();
        } else {
            setLoading(false);
        }
        fetchPuzzles();
    }, [loadUser, fetchPuzzles]);

    const handleLogin = async (email, password, isRegister = false) => {
        const endpoint = isRegister ? '/auth/register' : '/auth/login';
        try {
            const res = await api.post(endpoint, { email, password });
            setAuthToken(res.data.token);
            await loadUser();
            setCurrentPage('dashboard');
        } catch (err) {
            alert(err.response?.data?.msg || "An error occurred.");
        }
    };

    const handleSelectPuzzle = (puzzle) => {
        if (!isAuthenticated) {
            alert("Please sign in to solve puzzles.");
            setCurrentPage('signin');
            return;
        }
        setSelectedPuzzle(puzzle);
        setCurrentPage('puzzle');
    };

    const handlePuzzleSolved = async (solvedPuzzle, navigate = false) => {
        if (solvedPuzzle) {
            try {
                await loadUser();
            } catch (err) {
                console.error("Error updating user after solve", err);
            }
        }
        if (navigate) {
            setSelectedPuzzle(null);
            setCurrentPage('puzzles');
        }
    };

    const handleAddPuzzle = async (newPuzzle) => {
        try {
            await api.post('/puzzles', newPuzzle);
            await fetchPuzzles();
            alert('Puzzle added successfully!');
        } catch (err) {
            alert('Failed to add puzzle. Are you an admin?');
        }
    };

    const handleDeductXp = async (amount) => {
        try {
            const res = await api.post('/users/deduct-xp', { amount });
            setUser(res.data);
        } catch (err) {
            console.error("Failed to deduct XP", err);
        }
    };

    const renderPage = () => {
        if (loading) {
            return <div className="min-h-screen flex items-center justify-center bg-white dark:bg-black text-black dark:text-white">Loading...</div>;
        }
        if (currentPage === 'puzzle' && selectedPuzzle) {
            return <PuzzlePage puzzle={selectedPuzzle} onPuzzleSolved={handlePuzzleSolved} deductXp={handleDeductXp} />;
        }
        switch (currentPage) {
            case 'home':
                const featuredPuzzle = puzzles.find(p => p.featured) || puzzles[0];
                return <HomePage setCurrentPage={setCurrentPage} onSelectPuzzle={handleSelectPuzzle} isDarkMode={isDarkMode} dailyPuzzle={featuredPuzzle} />;
            case 'puzzles':
                return <PuzzlesPage puzzles={puzzles} onSelectPuzzle={handleSelectPuzzle} />;
            case 'dashboard':
                return isAuthenticated && user ? <DashboardPage user={user} /> : <AuthPage isSignIn={true} setCurrentPage={setCurrentPage} handleLogin={handleLogin} />;
            case 'admin':
                return isAdmin ? <AdminPage onAddPuzzle={handleAddPuzzle} /> : <RestrictedPage />;
            case 'signin':
                return <AuthPage isSignIn={true} setCurrentPage={setCurrentPage} handleLogin={(email, pass) => handleLogin(email, pass, false)} />;
            case 'signup':
                 return <AuthPage isSignIn={false} setCurrentPage={setCurrentPage} handleLogin={(email, pass) => handleLogin(email, pass, true)} />;
            default:
                return <HomePage setCurrentPage={setCurrentPage} onSelectPuzzle={handleSelectPuzzle} isDarkMode={isDarkMode} />;
        }
    };

    return (
        <div className={isDarkMode ? 'dark' : ''}>
            <div className="font-sans antialiased bg-white dark:bg-black">
                <Navbar 
                    currentPage={currentPage} 
                    setCurrentPage={setCurrentPage} 
                    isAdmin={isAdmin} 
                    isDarkMode={isDarkMode} 
                    toggleDarkMode={() => setIsDarkMode(!isDarkMode)}
                    isAuthenticated={isAuthenticated}
                    handleLogout={handleLogout}
                />
                <main>
                    {renderPage()}
                </main>
                <Footer />
            </div>
        </div>
    );
}
