const InteractiveGridBackground = ({ isDarkMode }) => {
    const canvasRef = useRef(null);
    const mouse = useRef({ x: -1000, y: -1000, radius: 100 });

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        let animationFrameId;
        let dots = [];

        const dotSpacing = 30;
        const dotSize = 2;
        const dotColor = isDarkMode
            ? 'rgba(107, 114, 128, 0.5)'
            : 'rgba(209, 213, 219, 0.7)';

        const resizeCanvas = () => {
            canvas.width = window.innerWidth; // ✅ Use full window size
            canvas.height = window.innerHeight;
            generateDots();
        };

        const generateDots = () => {
            dots = [];
            for (let x = dotSpacing / 2; x < canvas.width; x += dotSpacing) {
                for (let y = dotSpacing / 2; y < canvas.height; y += dotSpacing) {
                    dots.push({ x: x, y: y, ox: x, oy: y });
                }
            }
        };

        const handleMouseMove = (e) => {
            const rect = canvas.getBoundingClientRect();
            mouse.current.x = e.clientX - rect.left;
            mouse.current.y = e.clientY - rect.top;
        };

        const handleMouseLeave = () => {
            mouse.current.x = -1000;
            mouse.current.y = -1000;
        };

        window.addEventListener('resize', resizeCanvas);
        canvas.addEventListener('mousemove', handleMouseMove);
        canvas.addEventListener('mouseleave', handleMouseLeave);

        resizeCanvas(); // ✅ Initialize size & dots

        const animate = () => {
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            dots.forEach(dot => {
                const dx = mouse.current.x - dot.x;
                const dy = mouse.current.y - dot.y;
                const dist = Math.sqrt(dx * dx + dy * dy);

                if (dist < mouse.current.radius) {
                    const force = (mouse.current.radius - dist) / mouse.current.radius;
                    const directionX = dx / dist;
                    const directionY = dy / dist;
                    dot.x -= directionX * force * 6; // ✅ Stronger push for visibility
                    dot.y -= directionY * force * 6;
                } else {
                    dot.x += (dot.ox - dot.x) * 0.1;
                    dot.y += (dot.oy - dot.y) * 0.1;
                }

                ctx.beginPath();
                ctx.arc(dot.x, dot.y, dotSize, 0, Math.PI * 2);
                ctx.fillStyle = dotColor;
                ctx.fill();
            });

            animationFrameId = requestAnimationFrame(animate);
        };

        animate();

        return () => {
            cancelAnimationFrame(animationFrameId);
            window.removeEventListener('resize', resizeCanvas);
            canvas.removeEventListener('mousemove', handleMouseMove);
            canvas.removeEventListener('mouseleave', handleMouseLeave);
        };
    }, [isDarkMode]);

    return <canvas ref={canvasRef} className="fixed top-0 left-0 w-full h-full z-0" />;
};

