// --- MAIN APP COMPONENT ---
export default function App() {
    const [currentPage, setCurrentPage] = useState('home');
    const [selectedPuzzle, setSelectedPuzzle] = useState(null);
    const [isDarkMode, setIsDarkMode] = useState(false);

    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [currentUser, setCurrentUser] = useState(null);

    const [puzzles, setPuzzles] = useState(initialPuzzles);
    const [users, setUsers] = useState([]);

    const handleLogout = () => {
        setIsAuthenticated(false);
        setCurrentUser(null);
        setCurrentPage('home');
    };

    const handleAuth = (credentials, isSignIn) => {
        if (isSignIn) { // Login logic
            const foundUser = users.find(u => u.email === credentials.email && u.password === credentials.password);
            if (foundUser) {
                setCurrentUser(foundUser);
                setIsAuthenticated(true);
                setCurrentPage('dashboard');
            } else {
                alert("Invalid credentials.");
            }
        } else { // Signup logic
            if (users.some(u => u.email === credentials.email)) {
                alert("An account with this email already exists.");
                return;
            }
            const isAdmin = credentials.adminCode === 'ADMIN123';
            const newUser = {
                id: String(users.length + 1),
                email: credentials.email,
                password: credentials.password, // In a real app, this should be hashed
                isAdmin,
                xp: 0,
                difficultyBreakdown: { easy: 0, medium: 0, hard: 0 },
                recentlySolved: [],
            };
            setUsers(prev => [...prev, newUser]);
            setCurrentUser(newUser);
            setIsAuthenticated(true);
            setCurrentPage('dashboard');
        }
    };

    const handleSelectPuzzle = (puzzle) => {
        if (!isAuthenticated) {
            alert("Please sign in to solve puzzles.");
            setCurrentPage('signin');
            return;
        }
        setSelectedPuzzle(puzzle);
        setCurrentPage('puzzle');
    };

    const handlePuzzleSolved = (solvedPuzzle, navigate = false) => {
        if (solvedPuzzle) {
             setCurrentUser(prevUser => {
                const isAlreadySolved = prevUser.recentlySolved.some(p => p.puzzleId === solvedPuzzle.id);
                if (isAlreadySolved) {
                    return prevUser; 
                }

                const newRecentlySolved = [
                    ...prevUser.recentlySolved,
                    {
                        puzzleId: solvedPuzzle.id,
                        title: solvedPuzzle.title,
                        category: solvedPuzzle.category,
                        difficulty: solvedPuzzle.difficulty,
                        dateSolved: toLocalDateString(new Date()),
                    }
                ];

                const newDifficultyBreakdown = { ...prevUser.difficultyBreakdown };
                newDifficultyBreakdown[solvedPuzzle.difficulty.toLowerCase()] += 1;

                const updatedUser = {
                     ...prevUser,
                    xp: prevUser.xp + solvedPuzzle.xpReward,
                    recentlySolved: newRecentlySolved,
                    difficultyBreakdown: newDifficultyBreakdown,
                };

                setUsers(prevUsers => prevUsers.map(u => u.id === updatedUser.id ? updatedUser : u));

                setPuzzles(prevPuzzles =>
                    prevPuzzles.map(p =>
                        p.id === solvedPuzzle.id ? { ...p, solvedCount: p.solvedCount + 1 } : p
                    )
                );

                return updatedUser;
            });
        }

        if (navigate) {
            setSelectedPuzzle(null);
            setCurrentPage('puzzles');
        }
    };

    const handleAddPuzzle = (newPuzzle) => {
        const puzzleToAdd = {
            ...newPuzzle,
            id: String(puzzles.length + 2),
            solvedCount: 0,
            successRate: 0,
        };
        setPuzzles(prev => [...prev, puzzleToAdd]);
        alert('Puzzle added successfully!');
    };

    const handleDeletePuzzle = (puzzleId) => {
        if (window.confirm("Are you sure you want to delete this puzzle?")) {
            setPuzzles(prev => prev.filter(p => p.id !== puzzleId));
        }
    };

    const handleDeductXp = (amount) => {
         setCurrentUser(prevUser => {
            const updatedUser = { ...prevUser, xp: Math.max(0, prevUser.xp - amount) };
            setUsers(prevUsers => prevUsers.map(u => u.id === updatedUser.id ? updatedUser : u));
            return updatedUser;
        });
    };

    const renderPage = () => {
        const isAdmin = currentUser?.isAdmin || false;
        if (currentPage === 'puzzle' && selectedPuzzle) {
            return <PuzzlePage puzzle={selectedPuzzle} onPuzzleSolved={handlePuzzleSolved} deductXp={handleDeductXp} user={currentUser} />;
        }
        switch (currentPage) {
            case 'home':
                const featuredPuzzle = puzzles.find(p => p.featured) || puzzles[0];
                return <HomePage setCurrentPage={setCurrentPage} onSelectPuzzle={handleSelectPuzzle} isDarkMode={isDarkMode} dailyPuzzle={featuredPuzzle} />;
            case 'puzzles':
                return <PuzzlesPage puzzles={puzzles} onSelectPuzzle={handleSelectPuzzle} user={currentUser} />;
            case 'dashboard':
                return isAuthenticated && currentUser ? <DashboardPage user={currentUser} puzzles={puzzles} onSelectPuzzle={handleSelectPuzzle} setCurrentPage={setCurrentPage} isDarkMode={isDarkMode} /> : <AuthPage isSignIn={true} setCurrentPage={setCurrentPage} handleAuth={handleAuth} />;
            case 'admin':
                return isAdmin ? <AdminPage puzzles={puzzles} onAddPuzzle={handleAddPuzzle} onDeletePuzzle={handleDeletePuzzle} /> : <RestrictedPage />;
            case 'signin':
                return <AuthPage isSignIn={true} setCurrentPage={setCurrentPage} handleAuth={handleAuth} />;
            case 'signup':
                 return <AuthPage isSignIn={false} setCurrentPage={setCurrentPage} handleAuth={handleAuth} />;
            default:
                return <HomePage setCurrentPage={setCurrentPage} onSelectPuzzle={handleSelectPuzzle} isDarkMode={isDarkMode} />;
        }
    };

    return (
        <div className={isDarkMode ? 'dark' : ''}>
            <div className="font-sans antialiased bg-white dark:bg-black">
                <Navbar 
                    currentPage={currentPage} 
                    setCurrentPage={setCurrentPage} 
                    isAdmin={currentUser?.isAdmin || false} 
                    isDarkMode={isDarkMode} 
                    toggleDarkMode={() => setIsDarkMode(!isDarkMode)}
                    isAuthenticated={isAuthenticated}
                    handleLogout={handleLogout}
                />
                <main>
                    {renderPage()}
                </main>
                <Footer />
            </div>
        </div>
    );
}
